import React, { useState } from 'react';
import axios from 'axios';

interface ChatPageProps {
  setChatbotResponse: (text: string) => void;
}

export default function ChatPage({ setChatbotResponse }: ChatPageProps) {
  const [message, setMessage] = useState('');
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);

  // Filtros para generaci√≥n de caso
  const [pais, setPais] = useState('');
  const [sector, setSector] = useState('');
  const [tipoEmpresa, setTipoEmpresa] = useState('');
  const [tamanoEmpresa, setTamanoEmpresa] = useState('');

  const handleSend = async () => {
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5000/chat', {
        message,
      });

      const reply = res.data.respuesta || res.data.error;
      setResponse(reply);
      setChatbotResponse(reply);
    } catch (error) {
      console.error('Error enviando mensaje:', error);
      const errorMsg = '‚ùå Error al enviar la solicitud.';
      setResponse(errorMsg);
      setChatbotResponse(errorMsg);
    }
    setLoading(false);
  };

  const handleGenerateCase = async () => {
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5000/generar_caso', {
        pais,
        sector,
        tipo_empresa: tipoEmpresa,
        tamano_empresa: tamanoEmpresa,
      });

      const reply = res.data.caso_estudio || res.data.error;
      setResponse(reply);
      setChatbotResponse(reply);
    } catch (error) {
      console.error('Error generando caso de estudio:', error);
      const errorMsg = '‚ùå Error al generar el caso de estudio.';
      setResponse(errorMsg);
      setChatbotResponse(errorMsg);
    }
    setLoading(false);
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Chatbot ISO 9001</h1>

      <textarea
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Escribe tu mensaje..."
        className="w-full p-2 border rounded mb-4"
        rows={4}
      />

      <button
        onClick={handleSend}
        disabled={loading}
        className="bg-blue-700 hover:bg-blue-800 text-white px-4 py-2 rounded mb-6"
      >
        {loading ? 'Enviando...' : 'Enviar'}
      </button>

      <hr className="my-6" />

      <h2 className="text-xl font-semibold mb-4">üéØ Generar Caso de Estudio con Filtros</h2>

      <div className="grid grid-cols-2 gap-4 mb-4">
        <input
          value={pais}
          onChange={(e) => setPais(e.target.value)}
          placeholder="üåé Pa√≠s"
          className="p-2 border rounded"
        />
        <input
          value={sector}
          onChange={(e) => setSector(e.target.value)}
          placeholder="üè≠ √Årea o sector"
          className="p-2 border rounded"
        />
        <input
          value={tipoEmpresa}
          onChange={(e) => setTipoEmpresa(e.target.value)}
          placeholder="üè¢ Tipo de empresa"
          className="p-2 border rounded"
        />
        <input
          value={tamanoEmpresa}
          onChange={(e) => setTamanoEmpresa(e.target.value)}
          placeholder="üßç Tama√±o de empresa"
          className="p-2 border rounded"
        />
      </div>

      <button
        onClick={handleGenerateCase}
        disabled={loading}
        className="bg-purple-700 hover:bg-purple-800 text-white px-4 py-2 rounded"
      >
        {loading ? 'Generando...' : 'Generar caso de estudio'}
      </button>

      <div className="mt-6">
        <h2 className="font-semibold mb-2">üß† Respuesta:</h2>
        <div className="p-4 bg-gray-100 border rounded whitespace-pre-line">
          {response}
        </div>
      </div>
    </div>
  );
}
