import React from 'react';  // src/pages/ChatPage.tsx
import { useState } from 'react';
import axios from 'axios';

export default function ChatPage() {
  const [message, setMessage] = useState('');
  const [response, setResponse] = useState('');
  const [image, setImage] = useState<File | null>(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      setImage(e.target.files[0]);
    }
  };

  const handleSend = async () => {
    setLoading(true);
    try {
      const formData = new FormData();
      formData.append('message', message);
      if (image) formData.append('image', image);

      const res = await axios.post('http://localhost:5000/chat', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setResponse(res.data.respuesta || res.data.error);
    } catch (error) {
      console.error('Error enviando mensaje:', error);
      setResponse('❌ Error al enviar la solicitud.');
    }
    setLoading(false);
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Chatbot ISO 9001</h1>
      <textarea
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Escribe tu mensaje..."
        className="w-full p-2 border rounded mb-4"
        rows={4}
      />

      <input type="file" onChange={handleFileChange} className="mb-4" />

      <button
        onClick={handleSend}
        disabled={loading}
        className="bg-blue-700 hover:bg-blue-800 text-white px-4 py-2 rounded"
      >
        {loading ? 'Enviando...' : 'Enviar'}
      </button>

      <div className="mt-6">
        <h2 className="font-semibold mb-2">Respuesta:</h2>
        <div className="p-4 bg-gray-100 border rounded whitespace-pre-line">{response}</div>
      </div>
    </div>
  );
}
