import React, { useState } from 'react';
import ChatPage from './pages/ChatPage';
import ComparePage from './pages/ComparePage';
import axios from 'axios';

export default function App() {
  const [vista, setVista] = useState<'inicio' | 'comparar'>('inicio');
  const [chatbotResponse, setChatbotResponse] = useState('');

  const handleDescargarPDF = async () => {
    try {
      const payload = {
        caso_estudio: chatbotResponse,
        respuesta_ia: chatbotResponse,
        respuesta_usuario: '', // Puedes ajustar si tienes una respuesta del auditor guardada
        comparacion: 'Generado desde App',
      };

      const res = await axios.post('http://localhost:5000/descargar_pdf', payload, {
        responseType: 'blob',
      });

      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'informe_auditoria.pdf');
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (err) {
      alert('‚ùå Error al descargar el PDF.');
      console.error(err);
    }
  };

  return (
    <div className="h-screen flex flex-col">
      {/* Barra de navegaci√≥n */}
      <nav className="bg-gray-800 text-white p-4 flex gap-6 justify-center">
        <button
          onClick={() => setVista('inicio')}
          className={`hover:underline ${vista === 'inicio' ? 'font-bold underline' : ''}`}
        >
          üè† Inicio
        </button>
        <button
          onClick={() => setVista('comparar')}
          className={`hover:underline ${vista === 'comparar' ? 'font-bold underline' : ''}`}
        >
          üìä Comparar
        </button>
        <button
          onClick={handleDescargarPDF}
          className="hover:underline"
        >
          üì• Descargar PDF
        </button>
      </nav>

      {/* Contenido din√°mico */}
      <div className="flex-1 overflow-y-auto p-6">
        {vista === 'inicio' && <ChatPage setChatbotResponse={setChatbotResponse} />}
        {vista === 'comparar' && <ComparePage chatbotResponse={chatbotResponse} />}
      </div>
    </div>
  );
}
